func getFirstAddrForSeed(t *testing.T, seed string) (addr Address) {
    seedT,_:= ToTrytes(seed)
	assert.Equal(t,81,len(seedT))
	addresses, _ :=NewAddresses(seedT, 0, 1, 2)
	return addresses[0]
}

