func TestPowGo2(t *testing.T) {
	raw_tx := Transaction{
		SignatureMessageFragment:      "",
		Address:                       "LGOTCUDXMJQWGEDFUEEOMCIPDKCZSHNV9PQSIQLLMHMMVVXNPEGTYCHHSTITSUETK9LCXLVDIPXQWOYU9",
		Value:                         141358000,
		ObsoleteTag:                   "999999999999999999999999999",
		Timestamp:                     time.Unix(1514640651, 0),
		CurrentIndex:                  37,
		LastIndex:                     43,
		Bundle:                        "JPUOEFIGLPVYJLTXFCHLDQRJJTVDZKC9XDLCTYJBDGSNKNNJCP9DA9QYBNRDWG9WTTUGFDTLQIGLB9LJX",
		TrunkTransaction:              "HZBDRKHATOAOYYHRZTJIPAGHKAXEAGGBREKMFIAJXAX9GNMXEDI9PDUEKTM9VGBHXPSXTZ9CVXYL99999",
		BranchTransaction:             "TKZXZXNWFBLK9OXZGQ9UYEST9QQCJLWJJSHWNBUMNVWYQLTPWYNLSIMGCMAZHGTMOTN9EXFPURLG99999",
		Tag:                           "999999999999999999999999999",
		AttachmentTimestamp:           "PGXCATVJE",
		AttachmentTimestampLowerBound: "999999999",
		AttachmentTimestampUpperBound: "MMMMMMMMM",
		Nonce:                         "MPUBWMVRRJAHBQWOIEILQZLADXN",
	}
	assert.Equal(t, Trytes("SYLXZTEIBYKSYFELXJCFGRZNLUOLTARZYW9YJHRAVAUFYJLPYSEBTGZGENEMUVMEMMQQGIKHBUXC99999"), raw_tx.Hash())

	//MinWeightMagnitude
	for mwm := 0;  mwm <= DefaultMinWeightMagnitude+1; mwm++ {
		nonce, err := PowGo(raw_tx.Trytes(), mwm)
		assert.Nil(t, err)
		fmt.Printf("pow result : mwm = %d ; nonce = %v\n",mwm, nonce)
		/*
			pow result : mwm = 0 ;  nonce = 9FUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 1 ;  nonce = ZFUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 2 ;  nonce = KFUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 3 ;  nonce = KFUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 4 ;  nonce = DAUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 5 ;  nonce = AXUBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 6 ;  nonce = RCWBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 7 ;  nonce = GJZBWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 8 ;  nonce = WLPCWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 9 ;  nonce = WLPCWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 10 ; nonce = SSSHWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 11 ; nonce = SSSHWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 12 ; nonce = SSSHWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 13 ; nonce = SSSHWMVRRJAHBQWOIEILQZLADXN
			pow result : mwm = 14 ; nonce = IJHTZMVRRJAHBQWOIEILQZLADXN
		 */
		 assert.Equal(t,27,len(nonce))
		 assert.Equal(t,NonceTrinarySize/3,len(nonce))
		if mwm <= 5 {
			assert.Equal(t, nonce[len(nonce)-25:],  Trytes("UBWMVRRJAHBQWOIEILQZLADXN"), "error on mwm=%d",mwm)
		}
		if mwm <= 7 {
			assert.Equal(t, nonce[len(nonce)-24:],  Trytes("BWMVRRJAHBQWOIEILQZLADXN"), "error on mwm=%d",mwm)
		}
		if mwm > 7 && mwm <= 9 {
			assert.Equal(t, nonce, Trytes("WLPCWMVRRJAHBQWOIEILQZLADXN"))
		}
		if mwm > 9 && mwm <=13 {
			assert.Equal(t, nonce, Trytes("SSSHWMVRRJAHBQWOIEILQZLADXN"))
		}
		if mwm > 13 {
			assert.Equal(t, nonce, Trytes("IJHTZMVRRJAHBQWOIEILQZLADXN"),"error on mwm=%d",mwm)
		}
	}

}

func testPowGo(t *testing.T) {
	var tx Trytes = ""
	nonce, err := PowGo(tx, 14)
	if err != nil {
		t.Fatal(err)
	}
	tx = tx[:len(tx)-NonceTrinarySize/3] + nonce
	h := tx.Hash()
	if h[len(h)-4:] != "9999" {
		t.Error("pow is illegal", h)
	}else{
		fmt.Printf("pow result:\n\tnouce: %v\n\thash: %v\n",nonce,h)
	}
}

func TestPowGo(t *testing.T) {
	s := time.Now()
	testPowGo(t)
	ti := time.Now().Sub(s)
	sp := float64(countGo) / 1000 / ti.Seconds()
	t.Logf("%d kH/sec on Go PoW", int(sp))
}

func TestPowGo1(t *testing.T) {
	t.Logf("PowProcs:%d", PowProcs)
	proc := PowProcs
	PowProcs = 1
	testPowGo(t)
	PowProcs = proc
}
